Path: src/main/java/com/set08103/Report8_PopulationOfArea.java
 1	package com.set08103;
 2	
 3	import java.sql.Connection;
 4	import java.sql.PreparedStatement;
 5	import java.sql.ResultSet;
 6	import java.util.Scanner;
 7	
 8	public class Report8_PopulationOfArea {
 9	 public static void generate() {
 10	 Scanner scanner = new Scanner(System.in);
 11	
 12	 System.out.println("Select Area Type:");
 13	 System.out.println("1. Continent");
 14	 System.out.println("2. Region");
 15	 System.out.println("3. Country");
 16	 System.out.println("4. District");
 17	 System.out.println("5. City");
 18	 System.out.print("Enter choice (1-5): ");
 19	 int choice = scanner.nextInt();
 20	 scanner.nextLine(); // consume newline
 21	
 22	 String column = null;
 23	 String table = null;
 24	 switch (choice) {
 25	 case 1:
 26	 column = "continent";
 27	 table = "country";
 28	 break;
 29	 case 2:
 30	 column = "region";
 31	 table = "country";
 32	 break;
 33	 case 3:
 34	 column = "name";
 35	 table = "country";
 36	 break;
 37	 case 4:
 38	 column = "district";
 39	 table = "city";
 40	 break;
 41	 case 5:
 42	 column = "name";
 43	 table = "city";
 44	 break;
 45	 default:
 46	 System.out.println("Invalid choice.");
 47	 return;
 48	 }
 49	
 50	 System.out.print("Enter name of " + column + ": ");
 51	 String name = scanner.nextLine();
 52	
 53	 String query = String.format("SELECT SUM(population) AS total FROM %s WHERE %s = ?", table, column);
 54	
 55	 try {
 56	 Connection con = DBConnection.getConnection();
 57	 PreparedStatement ps = con.prepareStatement(query);
 58	 ps.setString(1, name);
 59	 ResultSet rs = ps.executeQuery();
 60	
 61	 if (rs.next()) {
 62	 long population = rs.getLong("total");
 63	 System.out.printf("Total population of %s '%s': %,d%n", column, name, population);
 64	 } else {
 65	 System.out.println("No data found.");
 66	 }
 67	
 68	 rs.close();
 69	 ps.close();
 70	 con.close();
 71	 } catch (Exception e) {
 72	 e.printStackTrace();
 73	 }
 74	 }
 75	}
