Path: src/main/java/com/set08103/Report9_LanguagePopulation.java
 1	package com.set08103;
 2	
 3	import java.sql.Connection;
 4	import java.sql.ResultSet;
 5	import java.sql.Statement;
 6	
 7	public class Report9_LanguagePopulation {
 8	 public static void generate() {
 9	 try {
 10	 Connection con = DBConnection.getConnection();
 11	
 12	 // Statement for language-wise population
 13	 Statement stmt1 = con.createStatement();
 14	
 15	 String query = "SELECT Language, SUM(Percentage * Country.Population / 100) AS Speakers " +
 16	 "FROM CountryLanguage " +
 17	 "JOIN Country ON Country.Code = CountryLanguage.CountryCode " +
 18	 "WHERE Language IN ('Chinese', 'English', 'Spanish','Urdu' ) " +
 19	 "GROUP BY Language";
 20	
 21	 ResultSet rs = stmt1.executeQuery(query);
 22	
 23	 // Statement for world population
 24	 Statement stmt2 = con.createStatement();
 25	 String worldPopQuery = "SELECT SUM(Population) AS TotalWorldPopulation FROM Country";
 26	 ResultSet rsWorld = stmt2.executeQuery(worldPopQuery);
 27	
 28	 long worldPopulation = 0;
 29	 if (rsWorld.next()) {
 30	 worldPopulation = rsWorld.getLong("TotalWorldPopulation");
 31	 }
 32	
 33	 System.out.println("Language Population Statistics:");
 34	 while (rs.next()) {
