Path: src/main/java/com/set08103/Report5_PopulationDistributionByCountry.java
 1	package com.set08103;
 2	
 3	
 4	import java.sql.Connection;
 5	import java.sql.ResultSet;
 6	import java.sql.Statement;
 7	
 8	public class Report5_PopulationDistributionByCountry {
 9	
 10	 public static void generate() {
 11	 try (Connection conn = DBConnection.getConnection()) {
 12	 String query = """
 13	 SELECT country.Name AS Country,
 14	 country.Population AS TotalPopulation,
 15	 IFNULL(SUM(city.Population), 0) AS PeopleInCities,
 16	 (country.Population - IFNULL(SUM(city.Population), 0)) AS PeopleNotInCities
 17	 FROM country
 18	 LEFT JOIN city ON country.Code = city.CountryCode
 19	 GROUP BY country.Name, country.Population
 20	 ORDER BY country.Name;
 21	 """;
 22	
 23	 Statement stmt = conn.createStatement();
 24	 ResultSet rs = stmt.executeQuery(query);
 25	
 26	 System.out.println("Population Distribution by Country:");
 27	 while (rs.next()) {
 28	 String name = rs.getString("Country");
 29	 int total = rs.getInt("TotalPopulation");
 30	 int inCities = rs.getInt("PeopleInCities");
 31	 int notInCities = rs.getInt("PeopleNotInCities");
 32	 double cityPercent = (total > 0) ? (inCities * 100.0 / total) : 0;
 33	 double nonCityPercent = (total > 0) ? (notInCities * 100.0 / total) : 0;
 34	
 35	 System.out.printf("%s | Total: %d | In Cities: %d (%.2f%%) | Not in Cities: %d (%.2f%%)%n",
 36	 name, total, inCities, cityPercent, notInCities, nonCityPercent);
 37	 }
 38	 } catch (Exception e) {
 39	 e.printStackTrace();
 40	 }
 41	 }
 42	}
