 61	 private static void generateCountryPopulation() {
 62	 try (Connection conn = DBConnection.getConnection()) {
 63	 String query = "SELECT Name, Population FROM country ORDER BY Population DESC LIMIT 10";
 64	 Statement stmt = conn.createStatement();
 65	 ResultSet rs = stmt.executeQuery(query);
 66	
 67	 System.out.println("Population by Country (Top 10):");
 68	 System.out.println("================================");
 69	 System.out.printf("%-25s %15s%n", "Country", "Population");
 70	 System.out.println("================================");
 71	
 72	 while (rs.next()) {
 73	 String country = rs.getString("Name");
 74	 long population = rs.getLong("Population");
 75	 System.out.printf("%-25s %,15d%n", country, population);
 76	 }
 77	 System.out.println();
 78	 } catch (Exception e) {
 79	 System.out.println("Error generating country population: " + e.getMessage());
 80	 }
 81	 }
 82	
 83	 private static void generateDistrictPopulation() {
 84	 try (Connection conn = DBConnection.getConnection()) {
 85	 String query = "SELECT District, SUM(Population) AS TotalPopulation FROM city GROUP BY District ORDER BY TotalPopulation DESC LIMIT 10";
 86	 Statement stmt = conn.createStatement();
 87	 ResultSet rs = stmt.executeQuery(query);
 88	
 89	 System.out.println("Population by District (Top 10):");
 90	 System.out.println("=================================");
 91	 System.out.printf("%-25s %15s%n", "District", "Population");
 92	 System.out.println("=================================");
 93	
 94	 while (rs.next()) {
 95	 String district = rs.getString("District");
 96	 long population = rs.getLong("TotalPopulation");
 97	 System.out.printf("%-25s %,15d%n", district, population);
 98	 }
 99	 System.out.println();
 100	 } catch (Exception e) {
 101	 System.out.println("Error generating district population: " + e.getMessage());
 102	 }
 103	 }
